import React, { useState, useEffect } from 'react';

const UserDevice = () => {
    const [devices, setDevices] = useState([]);
    const [userId, setUserId] = useState(null); // State variable to store the user ID

    useEffect(() => {
        // Fetch the user ID when the component mounts (you need to implement this part)
        fetchUserId();

        // The user ID will be set in the state variable userId
    }, []);

    useEffect(() => {
        // When the user ID is available, fetch the devices assigned to that user
        if (userId !== null) {
            fetchDevicesByUserId(userId);
        }
    }, [userId]);

    const fetchUserId = () => {
        // Implement this part to obtain the user ID of the logged-in user
        // This depends on your authentication system and how you store user information
        // For example, if you are using JSON Web Tokens (JWT), you can decode the token to get user information.
        // You might need to use a state management library like Redux or useContext to make user data available here.
        const token = localStorage.getItem('token');
        const decodedToken = JSON.parse(atob(token.split('.')[1]));
        const userId = decodedToken.userId;
        //setUserId(userId);

    };

    const fetchDevicesByUserId = (userId) => {
        const token = localStorage.getItem('token');
        const decodedToken = JSON.parse(atob(token.split('.')[1]));
        userId = decodedToken.userId;
        //setUserId(userId);
        console.log(userId)
        
        // Make an API request to your backend to fetch devices by user ID
        fetch(`http://localhost:8081/device/getDevicesByUserId/${userId}`)
            .then((response) => {
                if (response.status === 200) {
                    return response.json();
                    
                } else {
                    throw new Error('Failed to fetch devices by user ID');
                }
            })
            .then((data) => {
                console.log(data);
                setDevices(data);
            })
            .catch((error) => {
                console.error('Error:', error);
            });
    };

    return (
        <div>
            <h2>Devices Assigned to You</h2>
            <ul>
                {devices.map((device) => (
                    <li key={device.deviceId}>
                        <strong>Device Id:</strong> {device.deviceId}
                        <br />
                        <strong>Device Name:</strong> {device.device_name}
                        <br />
                        <strong>Address:</strong> {device.address}
                        <br />
                        <strong>Description:</strong> {device.description}
                        <br />
                        <strong>Max Hourly:</strong> {device.max_hourly}
                        <br />
                        <strong>User Id::</strong> {device.userId}
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default UserDevice;
