import React, { useState, useEffect } from 'react';

const UserDevice = () => {
    const [devices, setDevices] = useState([]);
    const [userId, setUserId] = useState(null); // State variable to store the user ID

    useEffect(() => {
        // Fetch the user ID when the component mounts
        fetchUserId();
    }, []);

    const fetchUserId = () => {
        // Obtain the user ID from the token and set it in the state
        const token = localStorage.getItem('token');
        const decodedToken = JSON.parse(atob(token.split('.')[1]));
        const userId = decodedToken.userId; // Make sure the key is correct

        setUserId(userId); // Set the user ID in the state
        // Now that you have the user ID, fetch the devices
        fetchDevicesByUserId(userId);
    };

    const fetchDevicesByUserId = (userId) => {
        // Make an API request to your backend to fetch devices by user ID
        fetch(`http://localhost:8081/device/getDevicesByUserId/${userId}`)
            .then((response) => {
                if (response.status === 200) {
                    return response.json();
                } else {
                    throw new Error('Failed to fetch devices by user ID');
                }
            })
            .then((data) => {
                setDevices(data);
            })
            .catch((error) => {
                console.error('Error:', error);
            });
    };

    return (
        <div>
            <h2>Devices Assigned to You</h2>
            <ul>
                {devices.map((device) => (
                    <li key={device.deviceId}>
                        <strong>Device Name:</strong> {device.device_name}
                        <br />
                        <strong>Address:</strong> {device.address}
                        <br />
                        <strong>Description:</strong> {device.description}
                        <br />
                        <strong>Max Hourly:</strong> {device.max_hourly}
                        <br />
                        <strong>User Id::</strong> {device.userId}
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default UserDevice;
