import React, { useState, useEffect } from 'react';
import '../users-page.css';
import { toast } from 'react-toastify';

const UpdateUser = ({ userId }) => {
  const [editedUser, setEditedUser] = useState(null);
  const [message, setMessage] = useState(null);

  useEffect(() => {
    // Fetch the user data when the component mounts
    fetchUser(userId);
  }, [userId]);

  const fetchUser = (userId) => {
    // Fetch the user data from your backend
    const token = localStorage.getItem('token');
    fetch(`http://localhost:8082/user/getUsers`, {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}`,
      },
    })
      .then((response) => {
        if (response.status === 200) {
          return response.json();
        } else if (response.status === 401) {
          // Handle Unauthorized access
          toast.error('Unauthorized access');
        } else {
          throw new Error('Failed to fetch data');
        }
      })
      .then((data) => {
        setEditedUser(data);
      })
      .catch((error) => {
        console.error('Error:', error);
      });
  };

  const handleUpdate = () => {
    if (editedUser) {
      const token = localStorage.getItem('token');
      fetch(`http://localhost:8082/user/update`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
        },
        body: JSON.stringify({
          name: editedUser.name,
          email: editedUser.email,
          // Include other fields as needed
        }),
      })
        .then((response) => {
          if (response.status === 200) {
            toast.success('User updated successfully.', {
              style: { background: '#90d490' },
              bodyClassName: 'custom-toast-content',
            });
          } else {
            toast.error('Failed to update user.', {
              style: { background: 'red' },
            });
          }
        })
        .catch((error) => {
          showMessage('An error occurred');
        });
    }
  };

  const showMessage = (text) => {
    setMessage(text);

    setTimeout(() => {
      setMessage(null);
    }, 2000);
  };

  return (
    <div>
      <h2>Update User</h2>
      {message && <p className="message">{message}</p>}
      {editedUser ? (
        <div>
          <label>Username:</label>
        <input
          type="text"
          value={editedUser.username} // Change name to username
          onChange={(e) =>
            setEditedUser({ ...editedUser, username: e.target.value }) // Change name to username
          }
        />
          <label>Email:</label>
        <input
          type="text"
          value={editedUser.email} // Change email to email
          onChange={(e) =>
            setEditedUser({ ...editedUser, email: e.target.value }) // Change email to email
          }
          />
          <label>Password:</label>
        <input
          type="text"
          value={editedUser.password} // Match the backend field name
          onChange={(e) =>
            setEditedUser({ ...editedUser, password: e.target.value }) // Match the backend field name
          }
          />
        <label>Role:</label>
        <input
          type="text"
          value={editedUser.role} // Match the backend field name
          onChange={(e) =>
            setEditedUser({ ...editedUser, role: e.target.value }) // Match the backend field name
          }
          />
        <label>Status:</label>
        <input
          type="text"
          value={editedUser.status} // Match the backend field name
          onChange={(e) =>
            setEditedUser({ ...editedUser, status: e.target.value }) // Match the backend field name
          }
          />
          {/* Add other fields as needed */}
          <button onClick={handleUpdate}>Save</button>
        </div>
      ) : (
        <p>Loading user data...</p>
      )}
    </div>
  );
};

export default UpdateUser;
