import React, { useState, useEffect } from 'react';
import '../users-page.css';
import { toast } from 'react-toastify';

const UpdateUser = ({ user, onSave, onCancel }) => {
  const [editedUser, setEditedUser] = useState(user);

  const handleUpdate = () => {
    // Implement the logic to update the user profile data.
    const token = localStorage.getItem('token');
    fetch('http://localhost:8082/user/update', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}`,
      },
      body: JSON.stringify({
        name: editedUser.name,
        email: editedUser.email,
        // Include other fields as needed
      }),
    })
      .then((response) => {
        if (response.status === 200) {
          toast.success('User updated successfully.', {
            style: { background: '#90d490' },
            bodyClassName: 'custom-toast-content',
          });
          onSave(editedUser); // Save the updated user data
        } else {
          toast.error('Failed to update user.', {
            style: { background: 'red' },
          });
        }
      })
      .catch((error) => {
        showMessage('An error occurred');
      });
  };

  const showMessage = (text) => {
    // Implement the message display logic
  };

  return (
    <div>
      <h3>Edit User Profile</h3>
      <label>Name:</label>
      <input
        type="text"
        value={editedUser.name}
        onChange={(e) => setEditedUser({ ...editedUser, name: e.target.value })}
      />
      <label>Email:</label>
      <input
        type="text"
        value={editedUser.email}
        onChange={(e) => setEditedUser({ ...editedUser, email: e.target.value })}
      />
      {/* Add other fields as needed */}
      <button onClick={handleUpdate}>Update</button>
      <button onClick={onCancel}>Cancel Edit</button>
    </div>
  );
};

export default UpdateUser;
