import React, { useState, useEffect } from 'react';
import '../users-page.css';
import { toast } from 'react-toastify';

const GetUsers = () => {
    const [users, setUsers] = useState([]);
    const [message, setMessage] = useState(null);
    const [editedUser, setEditedUser] = useState(null);
  
    useEffect(() => {
      // Fetch the list of users when the component mounts
      fetchUsers();
    }, []);
  
    const fetchUsers = () => {
      // Fetch the list of users from your backend
      const token = localStorage.getItem('token');
      fetch('http://localhost:8082/user/getUsers', {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
        },
      })
        .then((response) => {
          if (response.status === 200) {
            return response.json();
          } else {
            throw new Error('Failed to fetch data');
          }
        })
        .then((data) => {
          setUsers(data);
        })
        .catch((error) => {
          console.error('Error:', error);
        });
    };
    const showMessage = (text) => {
      setMessage(text);
  
      setTimeout(() => {
        setMessage(null);
      }, 2000);
    };

    const handleEdit = (user) => {
        setEditedUser(user);
      };
  
    const handleUpdate = () => {
    const token = localStorage.getItem('token');
    fetch(`http://localhost:8082/user/update/${editedUser.userId}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}`,
      },
      body: JSON.stringify({
        name: editedUser.name,
        email: editedUser.email,
      }),
    })
      .then((response) => {
        if (response.status === 200) {
          toast.success('User updated successfully.', {
            style: { background: '#90d490' },
            bodyClassName: 'custom-toast-content',
          });
          setEditedUser(null);
          fetchUsers();
        } else {
          toast.error('Failed to update user.', {
            style: { background: 'red' },
          });
        }
      })
      .catch((error) => {
        showMessage('An error occurred');
      });
  };
  
    const handleDelete = (userId) => {
      const token = localStorage.getItem('token');
      fetch(`http://localhost:8082/user/delete/${userId}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
        },
      })
        .then((response) => {
          if (response.status === 200) {
            toast.success('User deleted successfully.', {
                style: { background: '#90d490' },
                bodyClassName: 'custom-toast-content',
              });
            fetchUsers(); 
          } else {
            toast.error('Failed to delete user.', {
                style: { background: 'red' }, 
              });
          }
        })
        .catch((error) => {
          showMessage('An error occurred');
        });
    };
  
    return (
      <div>
        <h2>List of Users</h2>
        {message && <p className="message">{message}</p>}
        <div className="user-columns">
          {users.map((user) => (
            <div className="user-card" key={user.userId}>
              <h3>User: </h3>
              <p>Name: {user.name}</p>
              <p>Email: {user.email}</p>
              <div>
                <button
                  className="edit-delete-buttons"
                  type="button"
                  onClick={() => handleEdit(user.userId)}
                >
                  Edit
                </button>
                <button
                  className="edit-delete-buttons"
                  type="button"
                  onClick={() => handleDelete(user.userId)}
                >
                  Delete
                </button>
              </div>
            </div>
          ))}
        </div>
      </div>
    );
  };
  
  export default GetUsers;
