// LoginPage.js

import React, { useState } from 'react';
import '../index.css'; // Import the CSS

function LoginPage() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [isPopupOpen, setIsPopupOpen] = useState(false);

  const openPopup = () => {
    setIsPopupOpen(true);
  };

  const closePopup = () => {
    setIsPopupOpen(false);
  };

  const handleLogin = () => {
    // Add your login logic here, e.g., making an API request to authenticate the user.
    // Use the 'username' and 'password' state variables to access the input values.
    // You can perform your authentication and redirection to other pages here.
    console.log('Username:', username);
    console.log('Password:', password);
  };

  return (
    <div className={`modal ${isPopupOpen && 'active'}`}>
      <h2>Login Page</h2>
      <button onClick={openPopup}>Open Login Popup</button>

      <div className="popup-content">
        <input
          type="text"
          placeholder="Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <button onClick={closePopup}>Close</button>
        <button onClick={closePopup/* Add your login logic here */}>Login</button>
      </div>
    </div>
  );
}

export default LoginPage;
