import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const UsersPage = () => {
  const [users, setUsers] = useState([]);
  const [showUsers, setShowUsers] = useState(false);

  const fetchUsers = () => {
    const token = localStorage.getItem('token');
    // Fetch the list of users from your backend
    fetch('http://localhost:8082/user/getUsers', {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}`
      },
    })
      .then((response) => {
        if (response.status === 200) {
          return response.json(); // Only parse if the response status is 200 (OK).
        } else {
          throw new Error('Failed to fetch data');
        }
      })
      .then((data) => {
        console.log('Retrieved Users:', data);
        if (data && data.length > 0) {
          // Map the fetched data to UserDTO
          const mappedUsers = data.map((user) => ({
            userId: user.userId,
            username: user.username,
            email: user.email,
          }));
          setUsers(mappedUsers);
        } else {
          throw new Error('No users found.');
        }
      })
      .catch((error) => {
        console.error('Error:', error);
      });
  };

  const handleSeeAllUsers = () => {
    fetchUsers();
    setShowUsers(true);
  };

  return (
    <div>
      <h2>Welcome to the User Management Dashboard</h2>
      {showUsers ? (
        <div className="user-columns">
          {users.map((user) => (
            <div className="user-card" key={user.userId}>
              <h3>Username: {user.username}</h3>
              <p>Email: {user.email}</p>
              {/* Add more user information as needed */}
            </div>
          ))}
        </div>
      ) : (
        <Link to="/see-all-users" onClick={handleSeeAllUsers}>
          See All Users
        </Link>
      )}
    </div>
  );
};

export default UsersPage;
