import React, { useState, useEffect } from 'react';
import '../users-page.css';
import { toast } from 'react-toastify';

const GetUsers = () => {
  const [users, setUsers] = useState([]);
  const [message, setMessage] = useState(null);
  const [editedUser, setEditedUser] = useState(null);
  const [userRole, setUserRole] = useState(null); 

  useEffect(() => {
    // Fetch the list of users when the component mounts
    fetchUsers();
  }, []);

  const fetchUsers = () => {
    // Fetch the list of users from your backend
    const token = localStorage.getItem('token');
    fetch('http://localhost:8082/user/getUsers', {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}`,
      },
    })
      .then((response) => {
        if (response.status === 200) {
          return response.json();
        } else {
          throw new Error('Failed to fetch data');
        }
      })
      .then((data) => {
        setUsers(data);
        setUserRole(data.role);
      })
      .catch((error) => {
        console.error('Error:', error);
      });
  };

  const handleEdit = (user) => {
    setEditedUser(user);
  };

  const handleCancelEdit = () => {
    setEditedUser(null);
  };

  const handleUpdate = () => {
    const token = localStorage.getItem('token');
    fetch('http://localhost:8082/user/update', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}`,
      },
      body: JSON.stringify({
        name: editedUser.name,
        email: editedUser.email,
      }),
    })
      .then((response) => {
        if (response.status === 200) {
          toast.success('User updated successfully.', {
            style: { background: '#90d490' },
            bodyClassName: 'custom-toast-content',
          });
          setEditedUser(null);
          fetchUsers();
        } else {
          toast.error('Failed to update user.', {
            style: { background: 'red' },
          });
        }
      })
      .catch((error) => {
        showMessage('An error occurred');
      });
  };

  const showMessage = (text) => {
    setMessage(text);

    setTimeout(() => {
      setMessage(null);
    }, 2000);
  };

  const handleDelete = (userId) => {
    const token = localStorage.getItem('token');
    fetch(`http://localhost:8082/user/delete/${userId}`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}`,
      },
    })
      .then((response) => {
        if (response.status === 200) {
          toast.success('User deleted successfully.', {
            style: { background: '#90d490' },
            bodyClassName: 'custom-toast-content',
          });
          fetchUsers();
        } else {
          toast.error('Failed to delete user.', {
            style: { background: 'red' },
          });
        }
      })
      .catch((error) => {
        showMessage('An error occurred');
      });
  };

  return (
    <div>
      <h2>List of Users</h2>
      {message && <p className="message">{message}</p>}
      {userRole === 'admin' ? (
        // Display the list of users if the user is an admin
        <div className="user-columns">
          {users.map((user) => (
            <div className="user-card" key={user.userId}>
              <h3>User: </h3>
              {editedUser && editedUser.userId === user.userId ? (
                <div>
                  <label>Name:</label>
                  <input
                    type="text"
                    value={editedUser.name}
                    onChange={(e) =>
                      setEditedUser({ ...editedUser, name: e.target.value })
                    }
                  />
                  <label>Email:</label>
                  <input
                    type="text"
                    value={editedUser.email}
                    onChange={(e) =>
                      setEditedUser({ ...editedUser, email: e.target.value })
                    }
                  />
                  <button
                    className="edit-delete-buttons"
                    type="button"
                    onClick={handleUpdate}
                  >
                    Save
                  </button>
                  <button
                    className="edit-delete-buttons"
                    type="button"
                    onClick={handleCancelEdit}
                  >
                    Cancel
                  </button>
                </div>
              ) : (
                <div>
                  <p>Name: {user.name}</p>
                  <p>Email: {user.email}</p>
                  <div>
                    <button
                      className="edit-delete-buttons"
                      type="button"
                      onClick={() => handleEdit(user)}
                    >
                      Edit
                    </button>
                    <button
                      className="edit-delete-buttons"
                      type="button"
                      onClick={() => handleDelete(user.userId)}
                    >
                      Delete
                    </button>
                  </div>
                </div>
              )}
            </div>
          ))}
        </div>
      ) : (
        // Display "Access Restricted" message if the user is not an admin
        <p className="message">Access Restricted</p>
      )}
    </div>
  );
};

export default GetUsers;
