import React, { useState, useEffect } from 'react';

const GetUsers = () => {
  const [users, setUsers] = useState([]);
  const [message, setMessage] = useState(null);

  useEffect(() => {
    // Fetch the list of users when the component mounts
    fetchUsers();
  }, []);

  const fetchUsers = () => {
    // Fetch the list of users from your backend
    const token = localStorage.getItem('token');
    fetch('http://localhost:8082/user/getUsers', {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}`,
      },
    })
      .then((response) => {
        if (response.status === 200) {
          return response.json();
        } else {
          throw new Error('Failed to fetch data');
        }
      })
      .then((data) => {
        setUsers(data);
      })
      .catch((error) => {
        console.error('Error:', error);
      });
  };

  const handleEdit = (userId) => {
    // Handle edit action, e.g., navigate to an edit page.
  };

  const showMessage = (text) => {
    setMessage(text);

    // Automatically hide the message after 2 seconds (2000 milliseconds)
    setTimeout(() => {
      setMessage(null);
    }, 2000);
  };

  const handleDelete = (userId) => {
    const token = localStorage.getItem('token');
    fetch(`http://localhost:8082/user/deleteUser/${userId}`, {
      method: 'DELETE',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}`,
      },
    })
      .then((response) => {
        if (response.status === 200) {
          showMessage('User deleted successfully.');
          fetchUsers(); // Fetch the updated user list
        } else {
          showMessage('Failed to delete user');
        }
      })
      .catch((error) => {
        showMessage('An error occurred');
      });
  };

  return (
    <div>
      <h2>See All Users</h2>
      {message && <p className="message">{message}</p>}
      <div className="user-columns">
        {users.map((user) => (
          <div className="user-card" key={user.userId}>
            <h3>User: </h3>
            <p>Name: {user.name}</p>
            <p>Email: {user.email}</p>
            <div>
              <button
                className="edit-delete-buttons"
                type="button"
                onClick={() => handleEdit(user.userId)}
              >
                Edit
              </button>
              <button
                className="edit-delete-buttons"
                type="button"
                onClick={() => handleDelete(user.userId)}
              >
                Delete
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default GetUsers;
