import React, { useEffect, useState } from 'react';
import { over } from 'stompjs';
import SockJS from 'sockjs-client';
import '../chat.css';
import { useLocation } from 'react-router-dom';

var stompClient = null;

const ChatRoom = () => {
  const location = useLocation();
  const userEmail = location.state?.email || '';
  const [privateChats, setPrivateChats] = useState(new Map());
  const [publicChats, setPublicChats] = useState([]);
  const [tab, setTab] = useState('CHATROOM');
  const [loggedInUsers, setLoggedInUsers] = useState([]);
  const [typingStatus, setTypingStatus] = useState('');
  const [typingUsers, setIsTyping] = useState([]);
  const [userData, setUserData] = useState({
    username: userEmail,
    receivername: '',
    connected: false,
    message: '',
  });

  const sendTypingEvent = () => {
    if (stompClient && userData.username !== tab) {
      const typingData = {
        senderName: userData.username,
        receiverName: tab,
        status: 'TYPING',
      };

      stompClient.send('/app/typing', {}, JSON.stringify(typingData));
    }
  };

  const onPrivateMessage = (payload) => {
    var payloadData = JSON.parse(payload.body);
    const senderChats = privateChats.get(payloadData.senderName) || [];
    const updatedChats = [...senderChats, payloadData];
    setPrivateChats(new Map(privateChats.set(payloadData.senderName, updatedChats)));
  };


  const handleMessage = (event) => {
    const { value } = event.target;
    setUserData({ ...userData, message: value });
  };

  // const handleTyping = () => {
  //   setIsTyping(true);
  //   sendTypingEvent();

  // };

  // const handleTypingStopped = () => {
  //   setIsTyping(false);
  // };

  

  // const onTypingReceived = (payload) => {
  //   const typingData = JSON.parse(payload.body);
  //   if (typingData.status === 'TYPING') {
  //     const { senderName } = typingData;
  //     if (!typingUsers.includes(senderName)) {
  //       setIsTyping([...typingUsers, senderName]);
  //     }
  //   }
  // };

  const handleTyping = () =>
    socket.emit('typing', `${localStorage.getItem('email')} is typing`);

    <input
          type="text"
          placeholder="Write message"
          className="message"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          onKeyDown={handleTyping}
        />

        socket.on('typing', (data) => socket.broadcast.emit('typingResponse', data));    

  useEffect(() => {
    connect();
    console.log(userData);
    fetchLoggedInUsers();
    socket.on('typingResponse', (data) => setTypingStatus(data));
  }, []);

  const connect = () => {
    let Sock = new SockJS('http://localhost:8084/ws');
    stompClient = over(Sock);
    stompClient.connect({}, onConnected, onError);
  };

  const fetchLoggedInUsers = () => {
    const token = localStorage.getItem('token');
    fetch('http://localhost:8082/user/getLoggedInUsers', {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}`,
      },
    })
      .then((response) => response.json())
      .then((data) => {
        setLoggedInUsers(data); // Update the state with logged-in users' data
      })
      .catch((error) => {
        console.error('Error fetching logged-in users:', error);
      });
  };


  const onConnected = () => {
    setUserData({ ...userData, connected: true });
    stompClient.subscribe('/chatroom/public', onMessageReceived);
    stompClient.subscribe('/user/' + userData.username + '/private', onPrivateMessage);
    stompClient.subscribe('/user/' + userData.username + '/typing', sendTypingEvent);
    userJoin();
  };

  const userJoin = () => {
    var chatMessage = {
      senderName: userData.username,
      status: 'JOIN',
    };
    stompClient.send('/app/message', {}, JSON.stringify(chatMessage));
  };

  const onError = (err) => {
    console.log(err);

  }
  const onMessageReceived = (payload) => {
    var payloadData = JSON.parse(payload.body);
    switch (payloadData.status) {
      case "JOIN":
        if (!privateChats.get(payloadData.senderName)) {
          privateChats.set(payloadData.senderName, []);
          setLoggedInUsers((prevUsers) => [...prevUsers, payloadData.senderName]);
        }
        break;
      case "MESSAGE":
        publicChats.push(payloadData);
        setPublicChats([...publicChats]);
        break;
    }
  }




  const sendValue = () => {
    if (stompClient) {
      var chatMessage = {
        senderName: userData.username,
        message: userData.message,
        status: 'MESSAGE',
      };
      console.log(chatMessage);
      stompClient.send('/app/message', {}, JSON.stringify(chatMessage));
      setUserData({ ...userData, message: '' });
    }
  };

  const sendPrivateValue = () => {
    if (stompClient && userData.username !== tab) {
      var chatMessage = {
        senderName: userData.username,
        receiverName: tab,
        message: userData.message,
        status: 'MESSAGE',
      };

      if (userData.username !== tab) {
        privateChats.get(tab).push(chatMessage);
        setPrivateChats(new Map(privateChats));
      }
      stompClient.send('/app/private-message', {}, JSON.stringify(chatMessage));
      setUserData({ ...userData, message: '' });
    }
  };



  return (
    <div className="container">
      {stompClient && userData.connected ? (
        <div className="chat-box">
          <div className="member-list">
            <ul>
              <li
                onClick={() => {
                  setTab('CHATROOM');
                }}
                className={`member ${tab === 'CHATROOM' && 'active'}`}
              >
                Chatroom
              </li>
              {[...privateChats.keys()].map((name, index) => (
                <li
                  onClick={() => {
                    setTab(name);
                  }}
                  className={`member ${tab === name && 'active'}`}
                  key={index}
                >
                  {name}
                </li>
              ))}
            </ul>
          </div>
          {tab === 'CHATROOM' && (
            <div className="chat-content">
              <ul className="chat-messages">
                {publicChats.map((chat, index) => (
                  <li
                    className={`message ${chat.senderName === userData.username && 'self'
                      }`}
                    key={index}
                  >
                    {chat.senderName !== userData.username && (
                      <div className="avatar">{chat.senderName}</div>
                    )}
                    <div className="message-data">{chat.message}</div>
                    {chat.senderName === userData.username && (
                      <div className="avatar self">{chat.senderName}</div>
                    )}
                  </li>
                ))}
              </ul>
              <div className="typing-indicator">
              <p>Someone is typing...</p>
              </div>
              <div className="send-message">
                <input
                  type="text"
                  className="input-message"
                  placeholder="enter the message"
                  value={userData.message}
                  onChange={handleMessage}
                />
                <button
                  type="button"
                  className="send-button"
                  onClick={sendValue}
                >
                  send
                </button>
              </div>
            </div>
          )}
          {tab !== 'CHATROOM' && (
            <div className="chat-content">
              typingStatus={typingStatus}
              <ul className="chat-messages">
              <p>{typingStatus}</p>
                {[...privateChats.get(tab)].map((chat, index) => (
                  <li
                    className={`message ${chat.senderName === userData.username && 'self'
                      }`}
                    key={index}
                  >
                    {chat.senderName !== userData.username && (
                      <div className="avatar">{chat.senderName}</div>
                    )}
                    <div className="message-data">{chat.message}</div>
                    {chat.senderName === userData.username && (
                      <div className="avatar self">{chat.senderName}</div>
                    )}
                  </li>
                ))}
              </ul>

              <div className="send-message">
                <input
                  type="text"
                  className="input-message"
                  placeholder="enter the message"
                  value={userData.message}
                  onChange={handleMessage}
                />
                <button
                  type="button"
                  className="send-button"
                  onClick={sendPrivateValue}
                >
                  send
                </button>
              </div>
            </div>
          )}
        </div>
      ) : null}
    </div>
  );
};

export default ChatRoom;
