import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

const UsersPage = () => {
  const [users, setUsers] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Fetch the list of users from your backend
    fetch('http://localhost:8082/api/users', {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
      },
    })
      .then((response) => {
        if (response.status === 200) {
          return response.json(); // Only parse if the response status is 200 (OK).
        } else {
          throw new Error('Failed to fetch data');
        }
      })
      .then((data) => {
        if (data && data.length > 0) {
          // Map the fetched data to UserDTO
          const mappedUsers = data.map((user) => ({
            userId: user.userId,
            username: user.username,
            email: user.email,
          }));
          setUsers(mappedUsers);
        } else {
          setError('No users found.');
        }
      })
      .catch((error) => {
        console.error('Error:', error);
        setError('Failed to fetch data.');
      });
  }, []);

  if (error) {
    return <div>Error: {error}</div>;
  }

  return (
    <div>
      <h2>Welcome to the User Management Dashboard</h2>
      <Link to="/see-all-users">See All Users</Link>
      <ul>
        {users.map((user) => (
          <li key={user.userId}>
            Username: {user.username}, Email: {user.email}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default UsersPage;
