import React, { useState } from 'react';
import axios from 'axios'; // Import axios for making HTTP requests

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = () => {
    // Make a POST request to your backend login endpoint
    axios.post('http://your-backend-url/login', {
      username: username,
      password: password,
    })
    .then(response => {
      if (response.data.success) {
        // User successfully logged in
        alert('User logged in successfully');
        // You can also store the user token in local storage or state for future requests.
      } else {
        alert('Login failed. Please check your credentials.');
      }
    })
    .catch(error => {
      console.error('Error:', error);
    });
  };

  return (
    <div className="login-container">
      <h2>Welcome to Energy-Management System App</h2>
      <form>
        <div className="form-group">
          <label htmlFor="username">Username</label>
          <input type="text" id="username" name="username" value={username} onChange={(e) => setUsername(e.target.value)} />
        </div>
        <div className="form-group">
          <label htmlFor="password">Password</label>
          <input type="password" id="password" name="password" value={password} onChange={(e) => setPassword(e.target.value)} />
        </div>
        <button type="button" onClick={handleLogin}>Login</button> {/* Use onClick for the button */}
      </form>
    </div>
  );
};

export default Login;
